create table GAMES(
	ID INT,
	TITLE VARCHAR(10),
	THUMBNAIL VARCHAR(50),
	DESCRIPTION TEXT,
	FILE_PATH VARCHAR(50)
);


create table MOVIES (
	ID INT,
	TITLE VARCHAR(10),
	THUMBNAIL VARCHAR(50),
	DESCRIPTION TEXT,
	RATING VARCHAR(5),
	FILE_PATH VARCHAR(50),
	GENRE VARCHAR(6),
	RELEASE_DATE DATE,
	DURATION INT,
	DIRECTOR VARCHAR(50)
);
CREATE TABLE ORDERS (
    ORDER_ID      INT,
